# pylint: enable=line-too-long


class PKIClient:
    """
    Client for sending public key infrastructure (PKI) certificates to Ledger devices.
    This client allows sending various types of PKI certificates based on the key usage and device type.
    """
    _CLA: int = 0xB0
    _INS: int = 0x06

    def __init__(self, backend: BackendInterface) -> None:
        self._backend = backend

    def send_certificate(self, key_usage: PKIPubKeyUsage, from_CAL: bool = False) -> None:
        certificates = PKI_CERTIFICATES_CAL if from_CAL else PKI_CERTIFICATES
        if key_usage not in certificates:
            raise ValueError(f"Unsupported key usage {key_usage}")
        if self._backend.device.type not in certificates[key_usage]:
            raise ValueError(f"Unsupported device type for key usage {self._backend.device.type.name}")

        payload = certificates[key_usage][self._backend.device.type]
        assert payload, f"No certificate found for key usage {key_usage} on device {self._backend.device.type.name}"
        response = self.send_raw(key_usage, bytes.fromhex(payload))
        assert response.status == StatusWord.OK

    def send_raw(self, p1: PKIPubKeyUsage, payload: bytes) -> RAPDU:
        header = bytearray()
        header.append(self._CLA)
        header.append(self._INS)
        header.append(p1)
        header.append(0x00)
        header.append(len(payload))
        return self._backend.exchange_raw(header + payload)
